
public class SumofNatural {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i, num = 25, sum = 0;

		for (i = 5; i <= num; ++i) {
			// sum = sum + i;
			sum += i;
		}

		System.out.println("Sum of natural numbers between 5 to 25 = " + sum);
	}

}
